import React, { useState } from "react";
import AddCargo from "./components/AddCargo";
import CargoList from "./components/CargoList";

const App = () => {
  const [cargoList, setCargoList] = useState([
    {
      id: "CARGO001",
      name: "Строительные материалы",
      status: "В пути",
      origin: "Москва",
      destination: "Казань",
      departureDate: "2024-11-24",
    },
    {
      id: "CARGO002",
      name: "Хрупкий груз",
      status: "Ожидает отправки",
      origin: "Санкт-Петербург",
      destination: "Екатеринбург",
      departureDate: "2024-11-26",
    },
  ]);

  const addCargo = (newCargo) => {
    setCargoList((prevCargoList) => [...prevCargoList, newCargo]);
  };

  const updateStatus = (id, newStatus) => {
    setCargoList((prevCargoList) =>
      prevCargoList.map((cargo) =>
        cargo.id === id ? { ...cargo, status: newStatus } : cargo
      )
    );
  };

  return (
    <div>
      <AddCargo addCargo={addCargo} />
      <CargoList cargoList={cargoList} updateStatus={updateStatus} />
    </div>
  );
};

export default App;
