import React, { useState } from 'react';

const AddCargo = ({ addCargo }) => {
  const [cargo, setCargo] = useState({
    id: '',
    name: '',
    status: 'Ожидает отправки',
    origin: '',
    destination: '',
    departureDate: '',
  });

  // Обработчик изменений в форме
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCargo((prev) => ({ ...prev, [name]: value }));
  };

  // Обработчик отправки формы
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!cargo.name || !cargo.origin || !cargo.destination || !cargo.departureDate) {
      alert('Пожалуйста, заполните все поля');
      return;
    }

    // Генерация уникального id для груза
    const newCargo = { ...cargo, id: `CARGO${Date.now()}` };

    // Добавление нового груза в список
    addCargo(newCargo);

    // Очистка формы
    setCargo({
      id: '',
      name: '',
      status: 'Ожидает отправки',
      origin: '',
      destination: '',
      departureDate: '',
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Название груза</label>
        <input
          type="text"
          name="name"
          value={cargo.name}
          onChange={handleChange}
        />
      </div>

      <div>
        <label>Пункт отправления</label>
        <input
          type="text"
          name="origin"
          value={cargo.origin}
          onChange={handleChange}
        />
      </div>

      <div>
        <label>Пункт назначения</label>
        <input
          type="text"
          name="destination"
          value={cargo.destination}
          onChange={handleChange}
        />
      </div>

      <div>
        <label>Дата отправления</label>
        <input
          type="date"
          name="departureDate"
          value={cargo.departureDate}
          onChange={handleChange}
        />
      </div>

      <button type="submit">Добавить груз</button>
    </form>
  );
};

export default AddCargo;
