import React from 'react';

const CargoItem = ({ cargo, updateStatus }) => {
  const { id, name, status, origin, destination, departureDate } = cargo;

  const statusColor = status === 'Ожидает отправки' ? 'yellow' :
                      status === 'В пути' ? 'blue' : 'green';

  const handleChangeStatus = (e) => {
    const newStatus = e.target.value;
    if (newStatus === 'Доставлен' && new Date(departureDate) > new Date()) {
      alert('Груз не может быть доставлен, если дата отправления в будущем');
      return;
    }
    updateStatus(id, newStatus);
  };

  return (
    <tr style={{ backgroundColor: statusColor }}>
      <td>{id}</td>
      <td>{name}</td>
      <td>
        <select value={status} onChange={handleChangeStatus}>
          <option value="Ожидает отправки">Ожидает отправки</option>
          <option value="В пути">В пути</option>
          <option value="Доставлен">Доставлен</option>
        </select>
      </td>
      <td>{origin}</td>
      <td>{destination}</td>
      <td>{departureDate}</td>
    </tr>
  );
};

export default CargoItem;
